import {Component, OnInit} from '@angular/core';
import {EventModel} from "../../models/event.model";


@Component({
  selector: 'app-event',
  templateUrl: './event.component.html',
  styleUrls: ['./event.component.css']
})
export class EventComponent implements OnInit{


  events: EventModel[]=[
    {id:"iddd",name:"aaaaa"},
    {id:"id 2",name:"name 2"},
    {id:"id 2",name:"name 2"},
    {id:"id 2",name:"name 2"},
  ];

  constructor() {
  }
  ngOnInit():void {

  }

}

<div class="container">
  <h2>Upcoming Events</h2>
  <div class="row">
    <div class="col-md-4" *ngFor="let event of events">
      <div class="card">
        <img src="https://www.ligelaw.net/wp-content/uploads/2016/04/dummy-post-horisontal.jpg" class="card-img-top" alt="event.EventTitle ">
        <div class="card-body">
          <h5 class="card-title"> event.EventTitle </h5>
          <p class="card-text text-center">Start Date:  event.EventStartDate | date </p>
          <p class="card-text text-center">End Date:  event.EventEndDate | date </p>
          <p class="card-text text-center">Price:  event.Eventprice </p>
          <button class="btn btn-primary btn-block">Buy Ticket</button>
        </div>
      </div>
    </div>
  </div>
</div>
