import {Component, Input} from '@angular/core';
import {HttpClient} from "@angular/common/http";
import {environment} from "../../../environments/environment";
import {Router} from "@angular/router";

@Component({
  selector: 'app-payment',
  templateUrl: './payment.component.html',
  styleUrls: ['./payment.component.css']
})

export class PaymentComponent {
  cardholderName: string="";
  creditCardNumber: string="";
  expirationDate: string="";
  cvv: string="";
  amount: number=100;

  constructor(private http: HttpClient,private router: Router) {}
  baseApiUrl:string=environment.baseapiUrl;
  @Input() eventPrice: number = 0;

  makePayment() {
    const paymentRequest = {
      cardholderName: this.cardholderName,
      creditCardNumber: this.creditCardNumber,
      expirationDate: this.expirationDate,
      cvv: this.cvv,
      amount: this.amount,
    };

    this.http.post(this.baseApiUrl+'/api/payments/make-payment', paymentRequest).subscribe(
      (response: any) => {
        console.log('Payment successful:', response);
        this.router.navigate(['/success']);
        // Handle success response from the backend
      },
      (error) => {
        console.error('Payment failed:', error);
        // Handle error response from the backend
        this.router.navigate(['/error']);
      }
    );
  }
}
<div class="container mt-5">
  <div class="row justify-content-center">
    <div class="col-md-6">
      <div class="card">
        <div class="card-header">Payment Form</div>
        <div class="card-body">
          <form (ngSubmit)="makePayment()">
            <div class="form-group">
              <label for="cardholderName">Cardholder Name:</label>
              <input type="text" class="form-control" id="cardholderName" [(ngModel)]="cardholderName" name="cardholderName" required>
            </div>
            <div class="form-group">
              <label for="creditCardNumber">Credit Card Number:</label>
              <input type="text" class="form-control" id="creditCardNumber" [(ngModel)]="creditCardNumber" name="creditCardNumber" required>
            </div>
            <div class="form-row">
              <div class="form-group col-md-6">
                <label for="expirationDate">Expiration Date:</label>
                <input type="text" class="form-control" id="expirationDate" [(ngModel)]="expirationDate" name="expirationDate" required>
              </div>
              <div class="form-group col-md-6">
                <label for="cvv">CVV:</label>
                <input type="text" class="form-control" id="cvv" [(ngModel)]="cvv" name="cvv" required>
              </div>
            </div>
            <div class="form-group">
              <label for="amount">Amount:</label>
              <input type="number" class="form-control" id="amount" [(ngModel)]="amount" name="amount" readonly="readonly" required>
            </div>
            <button type="submit" class="btn btn-primary">Make Payment</button>
          </form>
        </div>
      </div>
    </div>
  </div>
</div>
