import {Component} from '@angular/core';
import {HttpClient} from "@angular/common/http";
import {Router} from "@angular/router";
import {environment} from "../../../environments/environment";
import {TicketModel} from "../../models/ticket.model";

@Component({
  selector: 'app-ticketinformation',
  templateUrl: './ticketinformation.component.html',
  styleUrls: ['./ticketinformation.component.css']
})
export class TicketinformationComponent {

  constructor(private http: HttpClient, private router: Router) {

  }

  baseApiUrl: string = environment.baseapiUrl;

  ticket:TicketModel={
    ticketId:"",
    name:"",
    eventId: "",
    amount:0,
  }

  ngOnInit(): void {

  }

  submit(name: string) {

    this.http.get<TicketModel>(this.baseApiUrl + '/api/payments/' + name).subscribe({
      next: (ticketRequest) => {
        this.ticket=ticketRequest;


      }
    });

  }

}
