import {Component, OnInit} from '@angular/core';
import {EventModel} from "../../models/event.model";


@Component({
  selector: 'app-event',
  templateUrl: './event.component.html',
  styleUrls: ['./event.component.css']
})
export class EventComponent implements OnInit{


  events: EventModel[]=[
    {id:"iddd",name:"aaaaa"},
    {id:"id 2",name:"name 2"},
  ];

  constructor() {
  }
  ngOnInit():void {

  }

}

<div class="container">
  <h2>Upcoming Events</h2>
  <div class="row">
    <div class="col-md-4" *ngFor="let event of events">
      <div class="card">

        <div class="card-body">
          <h5 class="card-title">event.EventTitle </h5>
          <p class="card-text">event.EventDescription </p>
          <p class="card-text">Price: event.Eventprice</p>
          <p class="card-text">Start Date: event.EventStartDate | date </p>
          <p class="card-text">End Date:  event.EventEndDate | date </p>
          <p class="card-text">Tickets Available:  event.EventNumberOfTicketAvailable </p>
          <p class="card-text">Created By: event.EventCreatedById </p>
        </div>
      </div>
    </div>
  </div>
</div>
