import {Component} from '@angular/core';
import {HttpClient} from "@angular/common/http";
import {ActivatedRoute, Router} from "@angular/router";
import {environment} from "../../../environments/environment";
import {TicketModel} from "../../models/ticket.model";

@Component({
  selector: 'app-ticketinformation',
  templateUrl: './ticketinformation.component.html',
  styleUrls: ['./ticketinformation.component.css']
})
export class TicketinformationComponent {

  constructor(private http: HttpClient, private router: Router,private route:ActivatedRoute) {

  }
  tickets: TicketModel[] = [];
  baseApiUrl: string = environment.baseapiUrl;

  ngOnInit(): void {
    this.route.paramMap.subscribe({
      next:(params)=>{
        const name= params.get("name");
        if(name){
          this.http.get<TicketModel[]>(this.baseApiUrl + '/api/payments/' + name).subscribe({
            next: (ticketRequest) => {
              this.tickets=ticketRequest;
              console.log(this.tickets)
            },error: (error) => {
              console.error('Error fetching tickets:', error);
              // Handle the error, e.g., display an error message to the user
            }
          });
        }
      }
    })

  }

  submit(name: string) {


  }

}
<p>ticketinformation works!</p>
<div class="container">
  <h1>Ticket Information</h1>

  <table class="table table-striped">
    <thead>
    <tr>
      <th>Ticket ID</th>
      <th>Name</th>
      <th>Event ID</th>
      <th>Amount</th>
    </tr>
    </thead>
    <tbody>
    <tr *ngFor="let ticket of tickets">
      <td>{{ ticket.ticketId }}</td>
      <td>{{ ticket.name }}</td>
      <td>{{ ticket.eventId }}</td>
      <td>{{ ticket.amount }}</td>
    </tr>
    </tbody>
  </table>
</div>
