import {Component} from '@angular/core';
import {HttpClient} from "@angular/common/http";
import {environment} from "../../../environments/environment";
import {TicketModel} from "../../models/ticket.model";

@Component({
  selector: 'app-ticketinformation',
  templateUrl: './ticketinformation.component.html',
  styleUrls: ['./ticketinformation.component.css']
})
export class TicketinformationComponent {

  constructor(private http: HttpClient) {

  }
  tickets: TicketModel[] = [];
  baseApiUrl: string = environment.baseapiUrl;
  searchText: string = '';

  searchTickets(){
    this.tickets=[]
    this.http.get<TicketModel[]>(this.baseApiUrl + '/api/payments/' + this.searchText).subscribe({
      next: (ticketRequest) => {
        this.tickets=ticketRequest;





      },error: (error) => {
        console.error('Error fetching tickets:', error);
        // Handle the error, e.g., display an error message to the user
      }
    });
  }


}
<div class="container text-center mt-4">
  <h1>Ticket Information</h1>

  <div class="form-group">
    <div class="input-group mb-3">
      <input type="text" [(ngModel)]="searchText" class="form-control" placeholder="Enter Ticket Name" />
      <div class="input-group-append">
        <button (click)="searchTickets()" class="btn btn-primary">Search</button>
      </div>
    </div>
  </div>

  <table class="table table-striped">
    <thead>
    <tr>
      <th>Ticket ID</th>
      <th>Name</th>
      <th>Event ID</th>
      <th>Amount</th>
    </tr>
    </thead>
    <tbody>
    <tr *ngFor="let ticket of tickets">
      <td>{{ ticket.ticketId }}</td>
      <td>{{ ticket.ticketName }}</td>
      <td>{{ ticket.ticketEventId }}</td>
      <td>{{ ticket.amount }}</td>
    </tr>
    <p *ngIf="tickets.length==0">No Tickets For The Name Provided</p>
    </tbody>
  </table>
</div>
