import {Component, OnInit} from '@angular/core';
import {ActivatedRoute} from "@angular/router";
import {EventsService} from "../../services/events.service";
import {EventModel} from "../../models/event.model";

@Component({
  selector: 'app-ticket',
  templateUrl: './ticket.component.html',
  styleUrls: ['./ticket.component.css']
})
export class TicketComponent implements OnInit{

  eventDetails:EventModel={
    id:"",
    eventId: '0',
    eventTitle: '0',
    eventDescription: '0',
    eventPrice: 1,
    eventVariations: '0',
    eventImage: '',
    eventStartDate: new Date('0'),
    eventEndDate: new Date('0'),
    eventNumberOfTicketAvailable: 0,
    eventCreatedById: '0',
  }
  constructor(private route:ActivatedRoute,private eventsService:EventsService) {
  }

  ngOnInit(): void {
    this.route.paramMap.subscribe({
      next:(params)=>{
        const id= params.get("id");
        if(id){
          this.eventsService.getEvent(id).subscribe({
            next:(response)=>{
              this.eventDetails=response;
              console.log(this.eventDetails);

            }
          });
        }


      }
    })

  }




}

<div class="container">
  <h2>Event Details</h2>
  <div class="card">
    <img [src]="eventDetails.eventImage" class="card-img-top" alt="{{ eventDetails.eventTitle }}">
    <div class="card-body">
      <h5 class="card-title">{{ eventDetails.eventTitle }}</h5>
      <p class="card-text">{{ eventDetails.eventDescription }}</p>
      <p class="card-text">Price: {{ eventDetails.eventPrice }} $</p>
      <p class="card-text">Variations: {{ eventDetails.eventVariations }}</p>
      <p class="card-text">Start Date: {{ eventDetails.eventStartDate | date }}</p>
      <p class="card-text">End Date: {{ eventDetails.eventEndDate | date }}</p>
      <p class="card-text">Available Tickets: {{ eventDetails.eventNumberOfTicketAvailable }}</p>
      <p class="card-text">Created By: {{ eventDetails.eventCreatedById }}</p>
    </div>
  </div>
</div>


<!-- Payment component section -->
<div class="card mt-3">
  <div class="card-body">
    <h3>Payment</h3>
    <app-payment></app-payment>
  </div>
</div>
