import {Component, OnInit} from '@angular/core';
import {EventModel} from "../../models/event.model";


@Component({
  selector: 'app-event',
  templateUrl: './event.component.html',
  styleUrls: ['./event.component.css']
})
export class EventComponent implements OnInit{


  events: EventModel[]=[
    {id:"iddd",name:"aaaaa"},
    {id:"id 2",name:"name 2"},
    {id:"id 2",name:"name 2"},
    {id:"id 2",name:"name 2"},
  ];

  constructor() {
  }
  ngOnInit():void {

  }

}


<div class="container">
  <h2>Upcoming Events</h2>
  <div class="row">
    <div class="col-md-4" *ngFor="let event of events">
      <div class="card">
        <div class="event-image">
          <img src="https://www.ligelaw.net/wp-content/uploads/2016/04/dummy-post-horisontal.jpg" class="card-img-top" alt=" event.EventTitle ">
          <div class="event-title">{{ event.EventTitle }</div>
          <div class="event-dates">
            <p>{{ event.EventStartDate | date } - { event.EventEndDate | date }</p>
          </div>
        </div>
        <div class="card-body">
          <p class="card-text">Price: {{ event.Eventprice }</p>
          <button class="btn btn-primary btn-block">Buy Ticket</button>
        </div>
      </div>
    </div>
  </div>
</div>
