import {Component, OnInit} from '@angular/core';
import {EventModel} from "../../models/event.model";
import {EventsService} from "../../services/events.service";


@Component({
  selector: 'app-event',
  templateUrl: './event.component.html',
  styleUrls: ['./event.component.css']
})
export class EventComponent implements OnInit{

  events: EventModel[]=[];

  constructor(private eventsService:EventsService) {
  }
  ngOnInit():void {
    this.eventsService.getAllEvents().subscribe({
      next:(e)=>{
        this.events = e;
        console.log(e)
      },error:(e)=>{
        console.log(e)
      }
    });

  }

}

<div class="container">
  <h2>Upcoming Events</h2>
  <div class="row">
    <div class="col-md-4 mb-3" *ngFor="let event of events">
      <div class="card">
        <img src="https://resources-biletino.s3-external-3.amazonaws.com/content/event/18070/960x540.jpg" class="card-img-top" alt="{{ event.EventTitle }">
        <div class="card-body">
          <h5 class="card-title"> {{event.eventTitle}}</h5>
          <p class="event-dates">
            24.09.2023-25.09.2023
          </p>
          <p class="card-text">Price: $100</p>
          <button class="btn btn-primary btn-block event-button" [routerLink]="[events]">Buy Ticket</button>
        </div>
      </div>
    </div>
  </div>
</div>
